pageContext.rootQuery:
query ProjectQuery($uid: String) {
  prismic {
    allProjects(uid: $uid) {
      edges {
        node {
          project_title
          project_preview_description
          project_preview_thumbnail
          project_category
          project_post_date
          project_hero_image
          project_description
          stack {
            technology
          }
          _meta {
            uid
          }
        }
      }
    }
  }
  site {
    siteMetadata {
      title
      description
      author
      image
    }
  }
}
